sys_script_client.script:
 // Define a function that triggers on change of a form control
function onChange(control, oldValue, newValue, isLoading) {
    // Check if the form is still loading or the new value is empty, then exit the function
    if (isLoading || newValue === '') {
        return;
    }
    // Retrieve the value of 'impact' from the form
    var impact = g_form.getValue('impact');
    // If 'impact' has a value, set the 'priority' field to '1'
    if (impact) {
        g_form.setValue('priority', '1');
        g_form.addInfoMessage('Priority set to 1 due to impact.');
    }
}
